import logo from './logo.svg';
import Quagga from 'quagga';
import { useEffect, useRef, useState} from 'react';
import './App.css';

function App() {
  const videoRef = useRef(null);
  const [error, setError] = useState(null);
  const [barcode, setBarcode] = useState(null);
  const [scanning, setScanning] = useState(true);

  const handleDetected = async (result) => {
    const code = result.codeResult.code;
    console.log('Barcode detected:', code);
    setBarcode(code);
    setScanning(false);
    Quagga.stop(); // Stop scanning after detection

    try {
      const response = await fetch('https://www.googleapis.com/books/v1/volumes?q=isbn:${code}');
      if (!response.ok) {
        throw new Error('Network resonse not ok');
      }
      const data = await response.json();
      if (data.totalItems > 0) {
        const book = data.items[0].volumeInfo;
        setBookInfo(book);
      } else {
        setError('No book found for the scanned barcode.');
      }
    } catch (err) {
      console.error('Error fetching book data:', err);
      setError('Failed to fetch book information. Please try again.');
    }
  };

  useEffect(() => {
    if (videoRef.current) {
      Quagga.init({
        inputStream: {
          name: "Live",
          type: "LiveStream",
          target: videoRef.current,
          constraints: {
            facingMode: "environment"
          }
        },

        decoder: {
          readers: ["code_128_reader",
                    "ean_reader",
                    "ean_8_reader",
                    "code_39_reader",
                    "code_39_vin_reader",
                    "codabar_reader",
                    "upc_reader",
                    "upc_e_reader",
                    "i2of5_reader"
          ]
        }
      }, function(err) {
        if (err) {
          console.error("Quagga initialization error:", err);
          setError("Failed to access the camera. Please allow camera permissions.")
          return;
        }
        Quagga.start()
      });

      Quagga.onDetected(handleDetected);

      return () => {
        Quagga.offDetected(handleDetected);
        Quagga.stop();
      };
    }
  }, [videoRef]);

  const handleScanAgain = () => {
    setBarcode(null);
    setScanning(true);
    Quagga.start();
  };

  return (
    <div className="App">
      <h1>Barcode Scanner</h1>
      {error && <p className="error">{error}</p>}
      {barcode ? (
        <div className="result">
        <p>Detected Barcode: {barcode}</p>
        <button onClick={handleScanAgain}>Scan Again</button>
      </div>
      ) : (
      <div className="scanner">
        <div ref={videoRef} className="scanner-video" autoPlay playsInline />
        {scanning && <p>Scanning for barcodes...</p>}
      </div>
      )}
    </div>
  );
}

export default App;
