import logo from './logo.svg';
import Quagga from 'quagga';
import { useEffect, useRef} from 'react';
import './App.css';

const videoElement = document.getElementById('barcode-scanner');

Quagga.init({
  inputStream: {
    name: "Live",
    type: "LiveStream",
    target: videoElement
  },

  decoder: {
    readers: ["code_128_reader"]
  }
}, function(err) {
  if (err) {
    console.error(err);
    return;
  }
  Quagga.start()
});

function App() {
  return (
    <div className="App">
       <video id="barcode-scanner" autoplay="true" playsinline="true"></video>
    </div>
  );
}

export default App;
